'use strict';

angular.module('app.config', [])

    .constant('appname', 'Myo2')

    .constant('version', 'v0.1.0')

    .constant('appdir', 'assets/app')

    .constant('apiUrl', '/api/v1') //you can set the whole URL here : e.g http://example.com/api/v1

    .constant('apiHeaders', {
        //read the auth token from the html (generated by the PHP part of Myo2)
        // this is why we have to include jquery ...
        'Authorization': $('meta[name="token_type"]').prop('content') + ' ' + $('meta[name="access_token"]').prop('content')
    }) //headers to send by default in your api calls (this is configured in myo.js)

    .config(function($urlRouterProvider, RestangularProvider, apiHeaders) {

        // For any unmatched url, redirect to /home
        $urlRouterProvider.otherwise('/home');
       //states are configured in each controller..

       //Configuring Restangular
       RestangularProvider.setDefaultHeaders(apiHeaders);
    });
